+Future<void> checkAndRunApi() async {
+  final SharedPreferences prefs = await SharedPreferences.getInstance();
+  final bool isFirstLaunch = prefs.getBool('isFirstLaunch') ?? true;
+
+  if (isFirstLaunch) {
+    await prefs.setBool('isFirstLaunch', false);
+
+    try {
