import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Plansheets Logs',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: const PlansheetsLogsScreen(),
    );
  }
}

class PlansheetsLogsScreen extends StatefulWidget {
  const PlansheetsLogsScreen({super.key});

  @override
  State<PlansheetsLogsScreen> createState() => _PlansheetsLogsScreenState();
}

class _PlansheetsLogsScreenState extends State<PlansheetsLogsScreen> {
  final List<Map<String, dynamic>> plansheetData = [
    {
      'sets': '100% DSA Submission',
      'drawingNo': 'A0-1',
      'revision': '10/27/2022',
      'revisionDate': '',
      'drawingTitle': 'OVERALL SITE PLAN',
      'discipline': 'Architectural',
    },
    {
      'sets': '100% DSA Submission',
      'drawingNo': 'A0-1.1',
      'revision': '10/27/2022',
      'revisionDate': '',
      'drawingTitle': 'SITE DEMOLITION',
      'discipline': 'Architectural',
    },
    {
      'sets': 'DSA Approved Plans - 8/21/23',
      'drawingNo': 'A0-1.1',
      'revision': 'Addendum 1',
      'revisionDate': 'Addendum 1',
      'drawingTitle': 'SITE DEMOLITION',
      'discipline': 'Architectural',
    },
    {
      'sets': '100% DSA Submission',
      'drawingNo': 'A0-2.1',
      'revision': '10/27/2022',
      'revisionDate': '',
      'drawingTitle': 'ENLARGED SITE PLAN',
      'discipline': 'Architectural',
    },
  ];

  List<bool> selectedSets = [false, false, false, false];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Plansheets Logs'),
      ),
      body: LayoutBuilder(
        builder: (context, constraints) {
          return SingleChildScrollView(
            child: Padding(
              padding: const EdgeInsets.all(16.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  // Filters Section
                  Row(
                    children: [
                      Expanded(
                        child: DropdownButtonFormField<String>(
                          decoration: const InputDecoration(
                            labelText: 'Plansheet Discipline',
                            border: OutlineInputBorder(),
                          ),
                          items: const [
                            DropdownMenuItem(
                              value: 'Architectural',
                              child: Text('Architectural'),
                            ),
                          ],
                          onChanged: (value) {},
                        ),
                      ),
                      const SizedBox(width: 16),
                      ElevatedButton(
                        onPressed: () {},
                        child: const Text('Clear Filter'),
                      ),
                    ],
                  ),
                  const SizedBox(height: 16),
                  // Search and Date Filters
                  Row(
                    children: [
                      Expanded(
                        child: TextField(
                          decoration: InputDecoration(
                            labelText: 'Search for drawing',
                            border: const OutlineInputBorder(),
                            suffixIcon: IconButton(
                              icon: const Icon(Icons.clear),
                              onPressed: () {},
                            ),
                          ),
                        ),
                      ),
                      const SizedBox(width: 16),
                      Expanded(
                        child: DropdownButtonFormField<String>(
                          decoration: const InputDecoration(
                            labelText: 'AHJ Revision',
                            border: OutlineInputBorder(),
                          ),
                          items: const [
                            DropdownMenuItem(
                              value: 'AHJ Revision',
                              child: Text('AHJ Revision'),
                            ),
                          ],
                          onChanged: (value) {},
                        ),
                      ),
                    ],
                  ),
                  const SizedBox(height: 16),
                  Row(
                    children: [
                      Expanded(
                        child: TextField(
                          decoration: const InputDecoration(
                            labelText: 'Revision Date',
                            border: OutlineInputBorder(),
                          ),
                        ),
                      ),
                      const SizedBox(width: 16),
                      Expanded(
                        child: Row(
                          children: [
                            Expanded(
                              child: TextField(
                                decoration: const InputDecoration(
                                  labelText: 'From Date (MM/DD/YYYY)',
                                  border: OutlineInputBorder(),
                                ),
                              ),
                            ),
                            const SizedBox(width: 8),
                            Expanded(
                              child: TextField(
                                decoration: const InputDecoration(
                                  labelText: 'To Date (MM/DD/YYYY)',
                                  border: OutlineInputBorder(),
                                ),
                              ),
                            ),
                          ],
                        ),
                      ),
                    ],
                  ),
                  const SizedBox(height: 16),
                  // Table Section
                  SingleChildScrollView(
                    scrollDirection: Axis.horizontal,
                    child: DataTable(
                      columns: const [
                        DataColumn(label: Text('#')),
                        DataColumn(label: Text('SETS')),
                        DataColumn(label: Text('DRAWING #')),
                        DataColumn(label: Text('REVISION')),
                        DataColumn(label: Text('AHJ RevisionDate')),
                        DataColumn(label: Text('DRAWING TITLE')),
                        DataColumn(label: Text('DISCIPLINE')),
                        DataColumn(label: Text('VIEW')),
                      ],
                      rows: List.generate(plansheetData.length, (index) {
                        final data = plansheetData[index];
                        return DataRow(
                          cells: [
                            DataCell(
                              Checkbox(
                                value: selectedSets[index],
                                onChanged: (value) {
                                  setState(() {
                                    selectedSets[index] = value ?? false;
                                  });
                                },
                              ),
                            ),
                            DataCell(Text(data['sets'])),
                            DataCell(Text(data['drawingNo'])),
                            DataCell(Text(data['revision'])),
                            DataCell(Text(data['revisionDate'])),
                            DataCell(Text(data['drawingTitle'])),
                            DataCell(Text(data['discipline'])),
                            DataCell(
                              IconButton(
                                icon: const Icon(Icons.visibility),
                                onPressed: () {},
                              ),
                            ),
                          ],
                        );
                      }),
                    ),
                  ),
                ],
              ),
            ),
          );
        },
      ),
    );
  }
}
